/* Print Styles */
@media print {
    .header,
    .footer,
    .promo-banner,
    .mobile-menu,
    .mobile-menu-overlay,
    .modal,
    .toast {
        display: none !important;
    }

    .container {
        max-width: none;
        padding: 0;
    }

    .btn {
        border: 1px solid #000;
        background: white !important;
        color: #000 !important;
    }

    a {
        color: #000 !important;
    }

    .hero,
    .promotion {
        background: white !important;
        color: #000 !important;
    }
}

/* High Contrast Mode */
@media (prefers-contrast: high) {
    .btn-primary {
        background: #000;
        border-color: #000;
        color: #fff;
    }

    .btn-secondary {
        background: #fff;
        border-color: #000;
        color: #000;
    }

    .product-card {
        border: 2px solid #000;
    }

    .modal-content {
        border: 2px solid #000;
    }
}

/* Reduced Motion */
@media (prefers-reduced-motion: reduce) {
    * {
        animation-duration: 0.01ms !important;
        animation-iteration-count: 1 !important;
        transition-duration: 0.01ms !important;
    }

    .btn:hover {
        transform: none !important;
    }

    .product-card:hover {
        transform: none !important;
    }

    .category-card:hover {
        transform: none !important;
    }
}

